// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

contract TeachMeContract {
    address payable public owner;

    enum ContractStatus {
        PENDING,
        ACTIVE,
        COMPLETED_TUTOR,
        COMPLETED_STUDENT,
        COMPLETED,
        CANCELLED
    }
    enum PaymentType {
        DEPOSIT,
        FEE_PER_SESSION
    }
    enum PaymentStatus {
        PENDING,
        SUCCESS,
        FAILED
    }

    struct Payment {
        string id;
        uint256 amount;
        uint256 feePercent;
        uint256 timestamp;
        PaymentType paymentType;
        PaymentStatus paymentStatus;
    }

    struct Contract {
        string id;
        string studentId;
        string tutorId;
        string classId;
        uint256 startDate;
        uint256 endDate;
        uint256 endDateActual;
        uint256 depositAmount;
        uint256 totalAmount;
        uint256 feePerSession;
        ContractStatus status;
        string[] paymentIds;
    }

    struct ContractParams {
        string id;
        string studentId;
        string tutorId;
        string classId;
        uint256 startDate;
        uint256 endDate;
        uint256 depositAmount;
        uint256 totalAmount;
        uint256 feePerSession;
    }

    struct ContractExtraParams {
        string grade;
        string subject;
        uint256 feePerHour;
        uint256 totalFee;
        bool mode;
        string[] schedules;
    }

    mapping(string => Contract) public contracts; // mapping t id hp ng n hp ng
    mapping(string => Payment) public payments; // mapping t id payment n payment
    mapping(string => string[]) contractToPayments; // mapping t id hp ng n danh sch cc payment

    event ContractCreated(
        string id,
        string studentId,
        string tutorId,
        string classId,
        uint256 startDate,
        uint256 endDate,
        uint256 depositAmount,
        uint256 totalAmount,
        uint256 feePerSession,
        string grade,
        string subject,
        uint256 feePerHour,
        uint256 totalFee,
        bool mode,
        string[] schedules
    );

    event ContractStatusUpdated(
        string id,
        ContractStatus previousStatus,
        ContractStatus newStatus,
        address updatedBy
    );

    event PaymentAdded(
        string contractId,
        string paymentId,
        uint256 amount,
        PaymentType paymentType,
        PaymentStatus paymentStatus
    );

    event WithdrawExecuted(address owner, uint256 amount, uint256 timestamp);

    event PaymentTuition(
        string paymentId,
        string contractId,
        uint256 paymentDate,
        uint256 amount,
        uint256 feePercent,
        uint256 priceVND
    );

    // Thit lp ngi deploy contract l owner
    constructor() {
        owner = payable(msg.sender);
    }

    // Modifier  kim tra ch owner mi c th gi cc function nht nh
    modifier onlyOwner() {
        require(
            msg.sender == owner,
            unicode'Ch ch s hu mi c th thc hin chc nng ny'
        );
        _;
    }

    // Modifier kim tra ngi gi l student ca contract
    modifier onlyStudent(string memory _contractId) {
        require(
            keccak256(abi.encodePacked(msg.sender)) ==
                keccak256(abi.encodePacked(contracts[_contractId].studentId)),
            unicode'Ch hc sinh mi c quyn thc hin chc nng ny'
        );
        _;
    }

    // Modifier kim tra ngi gi l tutor ca contract
    modifier onlyTutor(string memory _contractId) {
        require(
            keccak256(abi.encodePacked(msg.sender)) ==
                keccak256(abi.encodePacked(contracts[_contractId].tutorId)),
            unicode'Ch gia s mi c quyn thc hin chc nng ny'
        );
        _;
    }

    // Modifier kim tra ngi gi l student hoc tutor ca contract
    modifier onlyStudentOrTutor(string memory _contractId) {
        require(
            keccak256(abi.encodePacked(msg.sender)) ==
                keccak256(abi.encodePacked(contracts[_contractId].studentId)) ||
                keccak256(abi.encodePacked(msg.sender)) ==
                keccak256(abi.encodePacked(contracts[_contractId].tutorId)),
            unicode'Ch hc sinh hoc gia s mi c quyn thc hin chc nng ny'
        );
        _;
    }

    // Function cho php contract nhn tin
    receive() external payable {}

    function createContract(
        ContractParams memory params,
        ContractExtraParams memory extraParams
    ) public {
        Contract storage newContract = contracts[params.id];
        newContract.id = params.id;
        newContract.studentId = params.studentId;
        newContract.tutorId = params.tutorId;
        newContract.classId = params.classId;
        newContract.startDate = params.startDate;
        newContract.endDate = params.endDate;
        newContract.endDateActual = params.endDate;
        newContract.depositAmount = params.depositAmount;
        newContract.totalAmount = params.totalAmount;
        newContract.feePerSession = params.feePerSession;
        newContract.status = ContractStatus.PENDING;

        emit ContractCreated(
            params.id,
            params.studentId,
            params.tutorId,
            params.classId,
            params.startDate,
            params.endDate,
            params.depositAmount,
            params.totalAmount,
            params.feePerSession,
            extraParams.grade,
            extraParams.subject,
            extraParams.feePerHour,
            extraParams.totalFee,
            extraParams.mode,
            extraParams.schedules
        );
    }

    function updateContractStatus(
        string memory _contractId,
        ContractStatus _newStatus
    ) public onlyStudentOrTutor(_contractId) {
        Contract storage contractToUpdate = contracts[_contractId];
        ContractStatus currentStatus = contractToUpdate.status;

        // Kim tra iu kin chuyn i trng thi
        if (currentStatus == ContractStatus.PENDING) {
            // Student c th chuyn t PENDING sang ACTIVE hoc CANCELLED
            if (
                keccak256(abi.encodePacked(msg.sender)) ==
                keccak256(abi.encodePacked(contractToUpdate.studentId))
            ) {
                require(
                    _newStatus == ContractStatus.CANCELLED,
                    unicode'Hc sinh ch c th chuyn trng thi t PENDING sang ACTIVE hoc CANCELLED'
                );
            }
            // Tutor ch c th chuyn t PENDING sang CANCELLED
            else if (
                keccak256(abi.encodePacked(msg.sender)) ==
                keccak256(abi.encodePacked(contractToUpdate.tutorId))
            ) {
                require(
                    _newStatus == ContractStatus.CANCELLED,
                    unicode'Gia s ch c th chuyn trng thi t PENDING sang CANCELLED'
                );
            }
        } else if (currentStatus == ContractStatus.ACTIVE) {
            // Student ch c th chuyn t ACTIVE sang COMPLETED_STUDENT
            if (
                keccak256(abi.encodePacked(msg.sender)) ==
                keccak256(abi.encodePacked(contractToUpdate.studentId))
            ) {
                require(
                    _newStatus == ContractStatus.COMPLETED_STUDENT,
                    unicode'Hc sinh ch c th chuyn trng thi t ACTIVE sang COMPLETED_STUDENT'
                );

                // Nu tutor  nh du COMPLETED_TUTOR, chuyn sang COMPLETED
                if (contractToUpdate.status == ContractStatus.COMPLETED_TUTOR) {
                    _newStatus = ContractStatus.COMPLETED;
                }
            }
            // Tutor ch c th chuyn t ACTIVE sang COMPLETED_TUTOR
            else if (
                keccak256(abi.encodePacked(msg.sender)) ==
                keccak256(abi.encodePacked(contractToUpdate.tutorId))
            ) {
                require(
                    _newStatus == ContractStatus.COMPLETED_TUTOR,
                    unicode'Gia s ch c th chuyn trng thi t ACTIVE sang COMPLETED_TUTOR'
                );

                // Nu student  nh du COMPLETED_STUDENT, chuyn sang COMPLETED
                if (
                    contractToUpdate.status == ContractStatus.COMPLETED_STUDENT
                ) {
                    _newStatus = ContractStatus.COMPLETED;
                }
            }
        } else if (currentStatus == ContractStatus.COMPLETED_STUDENT) {
            // Ch tutor mi c th chuyn t COMPLETED_STUDENT sang COMPLETED
            require(
                keccak256(abi.encodePacked(msg.sender)) ==
                    keccak256(abi.encodePacked(contractToUpdate.tutorId)) &&
                    _newStatus == ContractStatus.COMPLETED,
                unicode'Ch gia s mi c th chuyn trng thi t COMPLETED_STUDENT sang COMPLETED'
            );
        } else if (currentStatus == ContractStatus.COMPLETED_TUTOR) {
            // Ch student mi c th chuyn t COMPLETED_TUTOR sang COMPLETED
            require(
                keccak256(abi.encodePacked(msg.sender)) ==
                    keccak256(abi.encodePacked(contractToUpdate.studentId)) &&
                    _newStatus == ContractStatus.COMPLETED,
                unicode'Ch hc sinh mi c th chuyn trng thi t COMPLETED_TUTOR sang COMPLETED'
            );
        } else {
            // Khng cho php thay i trng thi nu contract  COMPLETED hoc CANCELLED
            revert(
                unicode'Khng th thay i trng thi ca hp ng  hon thnh hoc  hy'
            );
        }

        // Cp nht trng thi v thi gian
        contractToUpdate.status = _newStatus;

        // Nu trng thi mi l COMPLETED, cp nht endDateActual
        if (_newStatus == ContractStatus.COMPLETED) {
            contractToUpdate.endDateActual = block.timestamp;
        }

        // Pht ra event khi cp nht trng thi
        emit ContractStatusUpdated(
            _contractId,
            currentStatus,
            _newStatus,
            msg.sender
        );
    }

    function paymentTutition(
        string memory _contractId,
        string memory _id,
        address payable recipient,
        uint256 amount,
        uint256 feePercent,
        uint256 priceVND
    ) public payable {
        require(msg.value > 0, unicode'Cn gi mt lng ETH ln hn 0');

        uint256 amountForTutor = msg.value - ((msg.value * feePercent) / 100);
        uint256 amountForOwner = (msg.value * feePercent) / 100;

        // Chuyn 50% cho ngi nhn
        recipient.transfer(amountForTutor);

        // Chuyn 50% cn li cho owner ca contract
        owner.transfer(amountForOwner);

        Payment storage newPayment = payments[_id];
        newPayment.id = _id;
        newPayment.amount = amount;
        newPayment.feePercent = feePercent;
        newPayment.timestamp = block.timestamp;
        newPayment.paymentType = PaymentType.FEE_PER_SESSION;
        newPayment.paymentStatus = PaymentStatus.SUCCESS;

        contractToPayments[_contractId].push(_id);

        emit PaymentTuition(
            _id,
            _contractId,
            block.timestamp,
            amount,
            feePercent,
            priceVND
        );
    }

    // Function  kim tra s d ca contract
    function getBalance() public view returns (uint) {
        return address(this).balance;
    }

    // Function rt ton b tin v v ca owner
    function withdraw() public onlyOwner {
        uint amount = address(this).balance;
        (bool success, ) = owner.call{value: amount}('');
        require(success, unicode'Chuyn tin tht bi');
    }

    // Function rt mt lng tin c th v v ca owner
    function withdrawAmount(uint _amount) public onlyOwner {
        require(_amount <= address(this).balance, unicode'S d khng ');
        (bool success, ) = owner.call{value: _amount}('');
        require(success, unicode'Chuyn tin tht bi');
    }

    function deposit(
        string memory _contractId,
        uint256 _ethVndRate
    ) public payable {
        Contract storage contractToDeposit = contracts[_contractId];

        // Kim tra contract tn ti v ang  trng thi PENDING
        require(
            bytes(contractToDeposit.id).length > 0,
            unicode'Hp ng khng tn ti'
        );
        require(
            contractToDeposit.status == ContractStatus.PENDING,
            unicode'Hp ng khng  trng thi ch t cc'
        );

        uint256 _depositAmount = msg.value * _ethVndRate;

        // Kim tra s tin gi vo
        require(
            contractToDeposit.depositAmount == _depositAmount,
            unicode'S tin t cc khng khp vi hp ng'
        );

        // To payment record mi
        string memory paymentId = string(
            abi.encodePacked(_contractId, '-deposit')
        );

        Payment storage newPayment = payments[paymentId];
        newPayment.id = paymentId;
        newPayment.amount = msg.value;
        newPayment.timestamp = block.timestamp;
        newPayment.paymentType = PaymentType.DEPOSIT;
        newPayment.paymentStatus = PaymentStatus.SUCCESS;

        // Thm payment vo contract
        contractToPayments[_contractId].push(paymentId);

        // Chuyn trng thi contract sang ACTIVE
        contractToDeposit.status = ContractStatus.ACTIVE;

        // Emit events
        emit PaymentAdded(
            _contractId,
            paymentId,
            msg.value,
            PaymentType.DEPOSIT,
            PaymentStatus.SUCCESS
        );

        emit ContractStatusUpdated(
            _contractId,
            ContractStatus.PENDING,
            ContractStatus.ACTIVE,
            msg.sender
        );
    }
}
